mapper.update:
UPDATE project_replyBoard SET
hit=hit-1
WHERE no=#{no}

UPDATE project_replyBoard SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,name,subject,content,hit,TO_CHAR(regdate,'YYYY-MM-DD') as dbday,group_id,group_step
FROM project_replyBoard
WHERE no=#{no}

mapper.update:
UPDATE project_replyBoard SET
isreply=1
WHERE no=#{no}
